const Gallery = ({ type, updateItemsEnabled, updateBlocksEnabled, updateMobsEnabled }) => {

  let items;
  switch(type) {
    case "item":
      items = ITEMS;
      break;
    case "block":
      items = BLOCKS;
      break;
    case "mob":
      items = MOBS;
      break;
    default:
      items = [];
  }

  return items.map(item => <Slot
    type={type}
    id={item}
    image={item}
    key={item}
    updateEnabled={
      type === "item" ? updateItemsEnabled :
      type === "block" ? updateBlocksEnabled :
      type === "mob" ? updateMobsEnabled :
      null
    }
  />)
}

export default Gallery